import React, { Component } from 'react';

class Counter extends Component {

    handleIncrement = (product) => {
        console.log(product);
        this.setState({count: this.state.count+1});
    };

    reset = () => {
        let count = this.state.count
        count = 0
        this.setState({count});
    };

    render() { 
        return (<div>
            <span className="badge badge-primary m-2">{this.formatCount()}</span>
            <button onClick={ ()=> this.handleIncrement(this.props.id)} className="btn">Increment</button>
            <React.Fragment>
            <button onClick={this.reset} className="btn1">Reset</button>
            <button onClick={()=>this.props.handleDelete(this.props.id)} className="btn1">Delete</button>
            </React.Fragment>  
            {/* <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul> */}
            </div>
            );
    }

    formatCount = ()=> {
        const {count} = this.state;
        return count === 0 ? "Zero" : count;
    }
}
 
export default Counter;
