import React, { Component } from 'react';

class Counter extends Component {
    states = {  
        count : 0
    }; 

    render() { 
        return (<div>
            <span>{this.formatCount()}</span><button>Increment</button>
            </div>  );
    }

    formatCount = ()=> {
        const {count} = this.states;
        return count === 0 ? "Zero" : count;
    }
}
 
export default Counter;
