import React, { Component } from 'react';
import Counter from './counter'

class Counters extends Component {
    state = { 
        counters:[
            {id:1,value:0},
            {id:2,value:0},
            {id:3,value:0},
            {id:4,value:0}
        ]
     }

     handleDelete = (value) =>{
         console.log("handleDelete is clicked");
         let counter = this.state.counters.filter(count => count.value!=counters.id );
         
     };

    render() { 
        return ( 
            <div>
                {this.state.counters.map(counter => <Counter key={counter.id}
                 value={counter.value} id={counter.id} handleDelete={()=>{this.handleDelete(counter.id)}}/>)}
            </div>
         );
    }
}
 
export default Counters;
