import React, { Component } from 'react';

class Counter extends Component {
    // state = {  
    //     count : this.props.value
    //     // tags: ["tag1","tag2","tag3"]
    // }; 

    // handleIncrement = (product) => {
    //     console.log(product);
    //     this.setState({count: this.state.count+1});
    // };

    // reset = () => {
    //     let count = this.state.count
    //     count = 0
    //     this.setState({count});
    // };

    render() { 
        return (<div>
            <span className="badge badge-primary m-2">{this.formatCount()}</span>
            <button onClick={()=> this.props.handleIncrement(this.props.counter)} className="btn">Increment</button>
            <React.Fragment>
            <button onClick={()=>this.props.reset()} className="btn1">Reset</button>
            <button onClick={()=>this.props.handleDelete(this.props.counter.id)} className="btn1">Delete</button>
            </React.Fragment>  
            {/* <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul> */}
            </div>
            );
    }

    formatCount = ()=> {
        const {value} = this.props.counter;
        return value === 0 ? "Zero" : value;
    }
}
 
export default Counter;
